import React, { Component } from 'react'
import { client } from './http/client/F1_index'
class Product extends Component {
    render(){
        if (this.props.id){
            let produtos = client.get(`/produtos/${this.props.id}`).then(response => { return response })
            return (
                <div>
                    { produtos.data.dados }
                </div>
            )
        }else{
            let produtos = client.get(`/produtos`).then(response => { return response })
            return (
                <div>
                    { produtos.data.dados }
                </div>
            )
        }
    }
  }

  export default Product;
